mapper.update:
update ADMIN
set AD_ACTIVE = #{AD_ACTIVE}
where AD_NO = #{AD_NO}

update CODE
set    ACTIVE = #{ACTIVE}
      , SEQ = #{SEQ}
where  CO_TYPE = #{CO_TYPE}
and	   CODE = #{CODE}

mapper.insert:
insert into PRODUCT
	(PRO_NO
	, PRO_WR
	, PRO_NAME
	, PRO_PRICE
	, PRO_CONTENT
	, PRO_CATE
	, PRO_STATUS
	, PRO_TC
	, PRO_TSC
	, PRO_DATE
	)
values (CONCAT('PR', IFNULL((SELECT MAX(CAST(replace(PRO_NO, 'PR', '') AS unsigned))+1
							 FROM PRODUCT A), 1))
	, #{MEM_ID}
	, #{PRO_NAME}
	, #{PRO_PRICE}
	, #{PRO_CONTENT}
	, #{PRO_CATE}
	, #{PRO_STATUS}
	, #{PRO_TC}
	, '거래전'
	, DATE_FORMAT(SYSDATE(), '%Y%m%d%H%i%s'))
