mapper.update:
update ADMIN
set AD_ACTIVE = #{AD_ACTIVE}
where AD_NO = #{AD_NO}

update CODE
set    ACTIVE = #{ACTIVE}
      , SEQ = #{SEQ}
where  CO_TYPE = #{CO_TYPE}
and	   CODE = #{CODE}

mapper.insert:
insert into PRODUCT
	(PRO_NO
	, PRO_WR
	, PRO_NAME
	, PRO_PRICE
	, PRO_CONTENT
	, PRO_CATE
	, PRO_STATUS
	, PRO_TC
	, PRO_TSC
	, PRO_DATE
	)
values (CONCAT('PR', IFNULL((SELECT MAX(CAST(replace(PRO_NO, 'PR', '') AS unsigned))+1
							 FROM PRODUCT A), 1))
	, #{MEM_ID}
	, #{PRO_NAME}
	, #{PRO_PRICE}
	, #{PRO_CONTENT}
	, #{PRO_CATE}
	, #{PRO_STATUS}
	, #{PRO_TC}
	, 'TM1'
	, DATE_FORMAT(SYSDATE(), '%Y%m%d%H%i%s'))

select.foreach:
select ACTIVE
  from CODE
 where 1 = 1
   and CO_TYPE = #{code.CO_TYPE} 
   and CO_NO = #{code.CO_NO}

update.foreach:
update CODE
   set CODE = #{code.CODE}
   
   	 , ACTIVE = '0'
   	 , HIDE = #{code.HIDE}
   
   
   	 , ACTIVE = #{code.ACTIVE}
   	 , HIDE = #{code.HIDE}
   
 where 1 = 1
   and CO_TYPE = #{code.CO_TYPE}
   and CO_NO = #{code.CO_NO}

update CODE
   set CODE = #{code.CODE}
   	 , HIDE = #{code.HIDE}
 where 1 = 1
   and CO_TYPE = #{code.CO_TYPE}
   and CO_NO = #{code.CO_NO}
