mapper.insert:
insert into MEMBERS
		( MEM_NO
		, MEM_ID
		, MEM_PW
		, MEM_GENDER
		, MEM_NAME
		, MEM_NICK
		, MEM_BIRTH
		, MEM_TEL
		, MEM_EMAIL
		, MEM_DATE
		, MEM_CAT
		, MEM_IMAGE )
values ( concat("ME", IFNULL((select MAX(cast(replace(MEM_NO, "ME", "") AS unsigned))+1 FROM MEMBERS A), 1))
		,#{MEM_ID}
		,#{MEM_PW}
		,#{MEM_GENDER}
		,#{MEM_NAME}
		,#{MEM_NICK}
		,#{MEM_BIRTH}
		,#{MEM_TEL}
		,#{MEM_EMAIL}
		,DATE_FORMAT(SYSDATE(), '%Y%m%d%H%i%s')
		,'0'
		,'profile.png' )

mapper.select:
select MEM_ID, MEM_PW, MEM_NICK
from MEMBERS
where MEM_ID = #{MEM_ID}
and MEM_PW = #{MEM_PW}
and MEM_CAT = '0'

select ROL_NO
from ADMIN
where AD_ID = #{AD_ID} 
and AD_PW = #{AD_PW}

select count(MEM_NICK)
from MEMBERS
where MEM_ID = #{MEM_ID}

select count(MEM_NICK)
from MEMBERS
where MEM_NICK = #{MEM_NICK}

select count(MEM_EMAIL)
from MEMBERS
where MEM_EMAIL = #{MEM_EMAIL}

select 
from MEMBERS
where 
	MEM_EMAIL = #{MEM_EMAIL} 
	AND MEM_NAME = #{MEM_NAME} 
	AND MEM_ID = #{MEM_ID}

mapper.update:
update MEMBERS
set MEM_NAME = #{MEM_NAME}
  , MEM_NICK = #{MEM_NICK}
  , MEM_PW = #{MEM_PW}
  , MEM_TEL = #{MEM_TEL}
  
  , MEM_IMAGE = #{MEM_IMAGE}
  
  , MEM_REDATE = DATE_FORMAT(SYSDATE(), '%Y%m%d%H%i%s')
where 1 = 1
and MEM_ID = #{MEM_ID}

update MEMBERS
set MEM_PW = #{MEM_PW}
where MEM_EMAIL = #{MEM_EMAIL}

mapper.select:
select MEM_NO, MEM_EMAIL
from MEMBERS
where MEM_EMAIL = #{MEM_EMAIL}
and MEM_NO = #{MEM_NO}

mapper.update:
update 	MEMBERS
set 	MEM_IMAGE = default
where 	MEM_ID = #{MEM_ID}

update PRODUCT
set PRO_TSC = 'TM3'
where PRO_NO = #{PRO_NO}
