mapper.insert:
insert into MEMBERS
		( MEM_NO
		, MEM_ID
		, MEM_PW
		, MEM_GENDER
		, MEM_NAME
		, MEM_NICK
		, MEM_BIRTH
		, MEM_TEL
		, MEM_EMAIL
		, MEM_DATE )
values ( concat("ME", IFNULL((select MAX(cast(replace(MEM_NO, "ME", "") AS unsigned))+1 FROM MEMBERS A), 1))
		,#{MEM_ID}
		,#{MEM_PW}
		,#{MEM_GENDER}
		,#{MEM_NAME}
		,#{MEM_NICK}
		,#{MEM_BIRTH}
		,#{MEM_TEL}
		,#{MEM_EMAIL}
		,DATE_FORMAT(SYSDATE(), '%Y%m%d%H%i%s') )

mapper.select:
select MEM_ID, MEM_PW
from MEMBERS
where MEM_ID = #{MEM_ID} 
and MEM_PW = #{MEM_PW}

select AD_ID, AD_PW
from ADMIN
where AD_ID = #{AD_ID} 
and AD_PW = #{AD_PW}
