mapper.insert:
INSERT INTO ADDRESS
	( ADD_NO
	, MEM_NO
	, ADD_POST
	, ADD_NAME
	, ADD_DETAIL
	, ADD_NICK
	, ADD_RECEIVER
	, ADD_PHONE)
VALUES 
	( 
	  (CONCAT("ADD", IFNULL( (SELECT MAX(CAST(REPLACE(ADD_NO,"ADD", "") AS UNSIGNED)) + 1 FROM ADDRESS A) , 1)))	 
	, #{MEM_NO}
	, #{ADD_POST}
	, #{ADD_NAME}
	, #{ADD_DETAIL}
	, #{ADD_NICK}
	, #{ADD_RECEIVER}
	, #{ADD_PHONE}
	)

mapper.select:
SELECT *
	FROM MEMBERS M
	JOIN ADDRESS A
		ON M.MEM_NO = A.MEM_NO
	WHERE M.MEM_NO = #{MEM_NO}
	AND A.ADD_NO = #{ADD_NO}

mapper.insert:
		INSERT INTO PAY
			( PAY_NO  
			 ,BUYER_NO
			 ,SELLER_NO
			 ,PRO_NO

			 ,PAY_METHOD 
			 ,CARD_NAME  
			 ,PAY_MSG  
			 ,BUYER_ADDR 
			 ,BUYER_NAME 
			 ,BUYER_POSTCODE  
			 ,BUYER_TEL  
			 ,MERCHANT_UID
			 ,NAME  
			 ,PAID_AMOUNT  
			 ,SUCCESS
			 ,PAY_TIME 

			 )
		VALUES
			((CONCAT("PAY", IFNULL( (SELECT MAX(CAST(REPLACE(PAY_NO,"PAY", "") AS UNSIGNED)) + 1 FROM PAY A) , 1)))
			 ,#{BUYER_NO}
			 ,#{SELLER_NO}
			 ,#{PRO_NO}

			 ,#{pay_method}
			 ,#{card_name}
			 ,#{PAY_MSG}
			 ,#{buyer_addr}
			 ,#{buyer_name}
			 ,#{buyer_postcode}
			 ,#{buyer_tel}
			 ,#{merchant_uid}
			 ,#{name}
			 ,#{paid_amount}
			 ,#{success}
			 ,DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')

			 )
